type Image struct {
	pDefaultImage     *ebiten.Image // default
	pTankUpImage      *ebiten.Image // tank
	pTankDownImage    *ebiten.Image
	pTankLeftImage    *ebiten.Image
	pTankRightImage   *ebiten.Image
	pBulletUpImage    *ebiten.Image // bullet
	pBulletDownImage  *ebiten.Image
	pBulletLeftImage  *ebiten.Image
	pBulletRightImage *ebiten.Image
}

func (pImage *Image) Init() {
	// tank
	pImage.pTankUpImage = NewEbitenImageByBytes(tankUpImageBytes)
	pImage.pTankDownImage = NewEbitenImageByBytes(tankDownImageBytes)
	pImage.pTankLeftImage = NewEbitenImageByBytes(tankLeftImageBytes)
	pImage.pTankRightImage = NewEbitenImageByBytes(tankRightImageBytes)

	// bullet
	pImage.pBulletUpImage = NewEbitenImageByBytes(bulletUpImageBytes)
	pImage.pBulletDownImage = NewEbitenImageByBytes(bulletDownImageBytes)
	pImage.pBulletLeftImage = NewEbitenImageByBytes(bulletLeftImageBytes)
	pImage.pBulletRightImage = NewEbitenImageByBytes(bulletRightImageBytes)
}

func NewEbitenImageByBytes(bs []byte) *ebiten.Image {
	img, _, err := image.Decode(bytes.NewBuffer(bs))
	if err != nil {
		log.Fatal(err)
	}

	pEbitenImage, err := ebiten.NewImageFromImage(img, ebiten.FilterDefault)
	if err != nil {
		log.Fatal(err)
	}

	return pEbitenImage
}

func NewEbitenImage(name string) *ebiten.Image {
	pFile, err := os.Open(name)
	if err != nil {
		log.Fatal(err)
	}

	// image: unknown format
	// 需要导入 import _ "image/gif"，image包不知道怎么Decode图片，需要导入 "image/gif" 去解码 gif 图片。
	img, _, err := image.Decode(pFile)
	if err != nil {
		log.Fatal(err)
	}

	pEbitenImage, err := ebiten.NewImageFromImage(img, ebiten.FilterDefault)
	if err != nil {
		log.Fatal(err)
	}

	return pEbitenImage
}

func OutputImage() {
	bytes, err := os.ReadFile("images/default.png")
	if err != nil {
		log.Fatal(err)
		return
	}

	str := string(bytes)
	dstFile, err := os.Create("C:\\Users\\xiangqian\\Desktop\\tmp\\test.txt")
	if err != nil {
		fmt.Println(err.Error())
		return
	}
	defer dstFile.Close()
	dstFile.WriteString(fmt.Sprintf("%q", str))
}
